import React from "react";

Tile.instances = [];
Tile.size = 100;
class Tile extends React.Component {
	constructor(props){
		super(props);
		this.x = props.x;
		this.y = props.y; 
		this.label = props.label;
		Tile.instances.push(this);
		this.state = {
			y: this.y,
			x: this.x
		}
	}

	render() {
		const top = `${this.state.y * Tile.size}px`;
   		const left = `${this.state.x * Tile.size}px`;
		return(
				<div className="tile" data-label={this.label} style={{top, left}}></div>
			);
	}
}

export default Tile;
