import React from "react";
import "./Piece.css";

class Piece extends React.Component {
	constructor(props) {
		super(props);
		this.number = props.number;
		this.x = props.x;
		this.y = props.y;
		this.callMeMaybe = props.callMe;
		this.state = {
			x: this.x,
			y: this.y
		}

		if (Piece.instances.includes(this) === false) Piece.instances.push(this);
	}

	render() {
		let theId = "_" + this.number;
		const left = `${this.x * Piece.size}px`;
		const top = `${this.y * Piece.size}px`;
		return (
			<div>
				<div className="piece" onClick={(event) => this.callMeMaybe(event) } draggable="true" data-number={this.number} id={theId} style={{top, left}}>
				{this.number}
				</div>
			</div>
			);
	}
}

Piece.size = 100;

export default Piece;
