import React from "react";
import "./Tile.css";

class Tile extends React.Component {
	constructor(props){
		super(props);
		this.x = props.x;
		this.y = props.y; 
		this.label = props.label;
		this.null = props.null;
		Tile.instances.push(this);
		this.state = {
			y: this.y,
			x: this.x
		}
	}

	render() {
		const top = `${this.state.y * Tile.size}px`;
   		const left = `${this.state.x * Tile.size}px`;
   		const height = `${Tile.size}px`;
   		const width = `${Tile.size}px`;
   		if (this.null === true) {
   			return null;
   		}
   		else {
		return(
				<div className="tile" data-label={this.label} style={{top, left, height, width}}></div>
			);
		}
	}
}
Tile.instances = [];
Tile.size = 100;

export default Tile;
