import React from 'react';
import Grid from './Grid';

class Tile extends React.Component {
	constructor(props) {
		super(props);
		Tile.instances.push(this);

		this.state = {
			x: 0,
			y: 0
		}

	}

	render() {
		const width = `${Tile.size}px`;
		const height = `${Tile.size}px`;
		const top = `${this.state.y * Tile.size}px`;
		const left = `${this.state.x * Tile.size}px`;
		return(
			<div className='tile'
				style={{ width, height, top, left }}
			></div>
			);
	}
}


Tile.instances = [];
Tile.size = 100;
Tile.randomize = () => {
	const cells = Grid.instance.cells.slice();

	for (let tile of Tile.instances) {
		const cellIndex = Math.floor(Math.random() * cells.length);
		const splicedCells = cells.splice(cellIndex, 1);

		const x = cell.x;
		const y = cell.y;

		tile.setState({ x, y });
	}
};

export default Tile;
