import React from "react";
import "./Tile.css";
import Piece from "./Piece";


class Tile extends React.Component {
	constructor(props){
		super(props);
		this.x = props.x;
		this.y = props.y; 
		this.label = props.label;
		this.null = props.null;
		this.number = props.number;
		this.everdayImShufflin = this.shuffle(Piece.instances.order);
		Tile.instances.push(this);
		this.state = {
			y: this.y,
			x: this.x
		}
	}

	// Shuffle using Fisher-Yates Algorithm
	shuffle(a) {
    	for (let i = a.length - 1; i > 0; i--) {
       		const j = Math.floor(Math.random() * (i + 1));
       		[a[i], a[j]] = [a[j], a[i]];
    	}
    	return a;
	}

	render() {
		const top = `${this.state.y * Tile.size}px`;
   		const left = `${this.state.x * Tile.size}px`;
   		const height = `${Tile.size}px`;
   		const width = `${Tile.size}px`;
   		if (this.null === true) {
   			console.log(this);
   			console.log(Tile.instances);
   			return "";

   		}
   		else {

			return(
					<div className="tile" data-label={this.label} style={{top, left, height, width}}>
						<Piece number={this.everdayImShufflin[this.number]} />
					</div>
				);
		}
	}
}
Tile.instances = [];
Tile.size = 100;

export default Tile;
