import React from 'react';

class Tile extends React.Component {
	constructor(props) {
		super(props);
		Tile.instances.push(this);
		this.props = props;
		this.state = {
			x: 0,
			y: 0
		}

	}

	render() {
		const width = `${Tile.size}px`;
		const height = `${Tile.size}px`;
		const top = `${this.state.y * Tile.size}px`;
		const left = `${this.state.x * Tile.size}px`;
		const backgroundImage = `url('img/${this.props.destination.x}_${this.props.destination.y}.png')`;
		return(
			<div className='tile'
				style={{ width, height, top, left, backgroundImage }}
				onClick={() => this.slide()  }
			></div>
			);
	}

	slide() {
		console.log('sliding');
	}

	get neighbors()
	{
		const top = Tile.get(this.state.x, this.state.y -1);
		const left = Tile.get(this.state.x - 1, this.state.y);
		const bottom = Tile.get(this.state.x, this.state.y+1);
		const right = Tile.get(this.state.x+1, this.state.y);
	}

	componentDidMount() {
		console.log("tile mounted" + this);
	}
}


Tile.instances = [];
Tile.size = 100;

export default Tile;
